import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { createPet } from '../api/api';
import "bootstrap/dist/css/bootstrap.min.css";
import '../App.css';

const AddPet = () => {
  const { id: ownerId } = useParams();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: '',
    birthDate: '',
    type: 'dog' // Valeur par défaut
  });

  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    setIsLoading(true);
    setError(null);

    try {
      const newPet = await createPet(ownerId, formData);
      console.log('New Pet Created:', newPet);

      navigate(`/owners/${ownerId}`);
    } catch (err) {
      setError('Error creating pet. Please try again.');
      console.error('Error creating pet:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <h3 style={{ marginBottom: "15px" }}><strong>New Pet</strong></h3>

      {error && <div className="alert alert-danger">{error}</div>}

      <form className='form-horizontal' onSubmit={handleSubmit}>
        <div className='form-group has-feedback'>
          <div className='form-group'>
            <label htmlFor='name'>Name</label>
            <input
              type='text'
              className='form-control'
              id='name'
              name='name'
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>

          <div className='form-group'>
            <label htmlFor='birthDate'>Birth Date</label>
            <input
              type='date'
              className='form-control'
              id='birthDate'
              name='birthDate'
              value={formData.birthDate}
              onChange={handleChange}
              required
            />
          </div>

          <div className='form-group'>
            <label htmlFor='type'>Type</label>
            <select
              className='form-control'
              id='type'
              name='type'
              value={formData.type}
              onChange={handleChange}
              required
            >
              <option value='bird'>Bird</option>
              <option value='cat'>Cat</option>
              <option value='dog'>Dog</option>
              <option value='hamster'>Hamster</option>
              <option value='lizard'>Lizard</option>
            </select>
          </div>
        </div>

        <div className='form-group'>
          <div className='col-sm-offset-2 col-sm-10'>
            <button
              className='add-pet-button'
              type='submit'
              disabled={isLoading}
              style={{ marginBottom: "45px" }}
            >
              {isLoading ? 'Adding...' : 'Add Pet'}
            </button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default AddPet;
