import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { createPet } from '../api/api';
import "bootstrap/dist/css/bootstrap.min.css";
import '../App.css';

const petTypes = ['bird', 'cat', 'dog', 'hamster', 'lizard'];

const AddPet = () => {
  const { id: ownerId } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    birthDate: '',
    type: petTypes[0],
  });
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);
    try {
      await createPet(ownerId, formData);
      navigate(`/owners/${ownerId}`);
    } catch (err) {
      setError('Error creating pet. Please try again.');
      console.error('Error creating pet:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container mt-4">
      <h3 style={{ marginBottom: "15px" }}><strong>Add New Pet</strong></h3>
      <form onSubmit={handleSubmit} className="form-horizontal">
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            className="form-control"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="birthDate">Birth Date</label>
          <input
            type="date"
            className="form-control"
            id="birthDate"
            name="birthDate"
            value={formData.birthDate}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="type">Type</label>
          <select
            className="form-control"
            id="type"
            name="type"
            value={formData.type}
            onChange={handleChange}
            required
          >
            {petTypes.map((type) => (
              <option key={type} value={type}>{type}</option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <button
            className="add-pet-button"
            type="submit"
            disabled={isLoading}
            style={{ marginTop: "20px" }}
          >
            {isLoading ? 'Adding...' : 'Add Pet'}
          </button>
        </div>
        {error && <div className="alert alert-danger">{error}</div>}
      </form>
    </div>
  );
};

export default AddPet;
